  sk.background(240);
  ball.update(sk);                  // moving ball updates
  wall.collideCircle(ball.pos, ball.vel, ball.radius, sk);
  wall.draw(sk);
  ball.draw(sk);
  staticBall.draw(sk);              // static ball is only drawn, not updated
};
```

